mapper.select:
SELECT ID ,LOCATION ,POWER FROM ( SELECT result1.* ,ROWNUM AS rnum FROM ( SELECT ID ,LOCATION ,POWER FROM KIOSK
WHERE power = #{power}
ORDER BY ID ASC ) result1 )
WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}

mapper.update:
UPDATE 
	KIOSK
SET 
	LOCATION = #{location}
	,POWER = #{power}
WHERE
	id = #{id}
