mapper.insert:
      INSERT INTO menu
	      (
	      	id
	      	,name
	      	,price
	      	,img_name
	      	,summary
	      	,description
	      	,is_sold
	      	,category_id
	      )
      VALUES
	      (
		      seq_menu_id.NEXTVAL
		      ,#{name}
		      ,#{price}
		      ,#{img_name}
		      ,#{summary}
		      ,#{description}
		      ,'false'
		      ,#{category_id}
	      )

mapper.select:
SELECT ID ,NAME ,PRICE ,IMG_NAME ,SUMMARY ,DESCRIPTION ,IS_SOLD ,CATEGORY_ID ,CATEGORY FROM ( SELECT result1.*, ROWNUM AS rnum FROM ( SELECT m.id AS id ,m.name AS name ,m.price AS price ,m.img_name AS img_name ,m.summary AS summary ,m.description as description ,m.is_sold AS is_sold ,m.category_id AS category_id ,c.CODE_NAME AS category FROM menu m JOIN common c ON m.CATEGORY_ID =c.CODE_ID
WHERE m.category_id = #{category_id}
) result1 ) WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}

mapper.update:

 UPDATE 
     menu 
 SET 
 	  name=#{name} 
	  ,price=#{price} 
	  ,img_name=#{img_name}
 	  ,summary=#{summary} 
 	  ,description=#{description}
 	  ,is_sold=#{is_sold}
 	  ,category_id=#{category_id}
 WHERE 
 	  id=#{id}
